changeSet.sql:
CREATE TABLE ROLES (
	ROLE_ID BIGINT NOT NULL,
	NAME CHARACTER VARYING(20) NOT NULL,		
	VERSION BIGINT DEFAULT (1)::BIGINT NOT NULL
);

CREATE TABLE USERS_ROLES (
	USER_ID BIGINT DEFAULT (1)::BIGINT NOT NULL,
	ROLE_ID BIGINT DEFAULT (1)::BIGINT NOT NULL
);

ALTER TABLE USERS_ROLES ADD CONSTRAINT USERS_ROLES_UK UNIQUE (USER_ID, ROLE_ID);
ALTER TABLE USERS_ROLES ADD CONSTRAINT USERS_ROLES_FK1 FOREIGN KEY (USER_ID) REFERENCES USERS (USER_ID) MATCH FULL;
ALTER TABLE USERS_ROLES ADD CONSTRAINT USERS_ROLES_FK2 FOREIGN KEY (ROLE_ID) REFERENCES ROLES (ROLE_ID) MATCH FULL;

CREATE TABLE PASSWORD_CHANGE_REQUESTS (
	PASSWORD_CHANGE_REQUEST_ID BIGINT NOT NULL,
	USER_ID BIGINT DEFAULT (1)::BIGINT NOT NULL,
	CREATION_DATE TIMESTAMPTZ NOT NULL
);
