CREATE SEQUENCE CONTRACTORS_SEQ START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1;
CREATE TABLE CONTRACTORS ( CONTRACTOR_ID INTEGER NOT NULL DEFAULT NEXTVAL('CONTRACTORS_SEQ'::REGCLASS), NAME TEXT NOT NULL, SYMBOL CHARACTER VARYING(70) NOT NULL, CONTRACTOR_GROUP_ID SMALLINT DEFAULT (0)::SMALLINT NOT NULL, TYPE SMALLINT DEFAULT (1)::SMALLINT NOT NULL, POSTCODE CHARACTER VARYING(6) NOT NULL, CITY CHARACTER VARYING(40) NOT NULL, ADRESS CHARACTER VARYING(60) NOT NULL, NIP CHARACTER VARYING(13) NOT NULL, DISCOUNT NUMERIC(4,2) DEFAULT 0.00 NOT NULL, ADDITIONAL CHARACTER VARYING(60) NOT NULL, ACCOUNT_NUMBER CHARACTER VARYING(34) NOT NULL, WEBSITE CHARACTER VARYING(60) NOT NULL, EMAIL CHARACTER VARYING(60) NOT NULL, COMMENTS TEXT NOT NULL, SOURCE_OF_MEMBER_ID SMALLINT DEFAULT (1)::SMALLINT NOT NULL, REPRESENTATIVE CHARACTER VARYING(70) NOT NULL, IS_COMPANY BOOLEAN DEFAULT FALSE, IS_ACTIVE BOOLEAN DEFAULT TRUE, VERSION BIGINT NOT NULL );
ALTER TABLE ONLY CONTRACTORS ADD CONSTRAINT CONTRACTORS_PK PRIMARY KEY (CONTRACTOR_ID); ALTER TABLE CONTRACTORS ADD CONSTRAINT CONTRACTORS_FK1 FOREIGN KEY (CONTRACTOR_GROUP_ID) REFERENCES CONTRACTORS_GROUPS (CONTRACTOR_GROUP_ID) MATCH FULL; ALTER TABLE CONTRACTORS ADD CONSTRAINT CONTRACTORS_FK2 FOREIGN KEY (SOURCE_OF_MEMBER_ID) REFERENCES SOURCES_OF_MEMBER (SOURCE_OF_MEMBER_ID) MATCH FULL; ALTER SEQUENCE CONTRACTORS_SEQ OWNED BY CONTRACTORS.CONTRACTOR_ID; SELECT PG_CATALOG.SETVAL('CONTRACTORS_SEQ', 1, TRUE);

CREATE TABLE POSTAL_CODES (
CODE CHARACTER(6) NOT NULL,
