);
ALTER TABLE ONLY POSTAL_CODES ADD CONSTRAINT POSTAL_CODES_PK PRIMARY KEY (CODE);

CREATE SEQUENCE GOODS_GROUPS_SEQ
START WITH 1
INCREMENT BY 1
NO MINVALUE
NO MAXVALUE
CACHE 1;

CREATE TABLE GOODS_GROUPS (
GOOD_GROUP_ID SMALLINT DEFAULT NEXTVAL('GOODS_GROUPS_SEQ'::REGCLASS) NOT NULL,
NAME CHARACTER VARYING(60) NOT NULL,
IS_ACTIVE BOOLEAN DEFAULT TRUE NOT NULL,
VERSION BIGINT NOT NULL
);

ALTER TABLE GOODS_GROUPS OWNER TO admin;
ALTER TABLE ONLY GOODS_GROUPS ADD CONSTRAINT GOODS_GROUPS_PK PRIMARY KEY (GOOD_GROUP_ID);

ALTER TABLE GOODS_GROUPS_SEQ OWNER TO admin;
ALTER SEQUENCE GOODS_GROUPS_SEQ OWNED BY GOODS_GROUPS.GOOD_GROUP_ID;
SELECT PG_CATALOG.SETVAL('GOODS_GROUPS_SEQ', 1, TRUE);

CREATE SEQUENCE GOODS_SEQ START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1;
CREATE TABLE GOODS ( GOOD_ID INTEGER DEFAULT NEXTVAL('GOODS_SEQ'::REGCLASS) NOT NULL, SYMBOL CHARACTER VARYING(70) NOT NULL, NAME CHARACTER VARYING(70) NOT NULL, TYPE SMALLINT DEFAULT (1)::SMALLINT NOT NULL, TAX_ID SMALLINT DEFAULT (1)::SMALLINT NOT NULL, UNIT_ID SMALLINT DEFAULT (1)::SMALLINT NOT NULL, PKWIU CHARACTER VARYING(25) NOT NULL, GOOD_GROUP_ID SMALLINT DEFAULT (1)::SMALLINT NOT NULL, PRICE_A_NET NUMERIC(10,2) NOT NULL, PRICE_A_GROSS NUMERIC(10,2) NOT NULL, PRICE_MAG_NET NUMERIC(10,2) NOT NULL, PRICE_MAG_GROSS NUMERIC(10,2) NOT NULL, PRICE_B_NET NUMERIC(10,2) NOT NULL, PRICE_B_GROSS NUMERIC(10,2) NOT NULL, PRICE_C_NET NUMERIC(10,2) NOT NULL, PRICE_C_GROSS NUMERIC(10,2) NOT NULL, DESCRIPTION TEXT NOT NULL, PHOTO CHARACTER VARYING(180) NOT NULL, WEIGHT DOUBLE PRECISION DEFAULT (0)::DOUBLE PRECISION NOT NULL, IS_ACTIVE BOOLEAN DEFAULT TRUE NOT NULL, VERSION BIGINT NOT NULL );
ALTER TABLE ONLY GOODS ADD CONSTRAINT GOODS_PK PRIMARY KEY (GOOD_ID); ALTER TABLE GOODS ADD CONSTRAINT GOODS_FK1 FOREIGN KEY (TAX_ID) REFERENCES TAXES (TAX_ID) MATCH FULL; ALTER TABLE GOODS ADD CONSTRAINT GOODS_FK2 FOREIGN KEY (UNIT_ID) REFERENCES UNITS (UNIT_ID) MATCH FULL; ALTER TABLE GOODS ADD CONSTRAINT GOODS_FK3 FOREIGN KEY (GOOD_GROUP_ID) REFERENCES GOODS_GROUPS (GOOD_GROUP_ID) MATCH FULL;
ALTER SEQUENCE GOODS_SEQ OWNED BY GOODS.GOOD_ID; SELECT PG_CATALOG.SETVAL('GOODS_SEQ', 1, TRUE);
