CREATE TABLE ROLES (
	ROLE_ID BIGINT NOT NULL,
	ROLE_NAME CHARACTER VARYING(20) NOT NULL,		
	VERSION BIGINT DEFAULT (1)::BIGINT NOT NULL
);

CREATE TABLE USERS_ROLES (
	USER_ID BIGINT NOT NULL,
	ROLE_ID BIGINT NOT NULL
);

ALTER TABLE USERS_ROLES ADD CONSTRAINT USERS_ROLES_UK UNIQUE (USER_ID, ROLE_ID);
ALTER TABLE USERS_ROLES ADD CONSTRAINT USERS_ROLES_FK1 FOREIGN KEY (USER_ID) REFERENCES USERS (USER_ID);
ALTER TABLE USERS_ROLES ADD CONSTRAINT USERS_ROLES_FK2 FOREIGN KEY (ROLE_ID) REFERENCES ROLES (ROLE_ID);

            ALTER TABLE pending_invitations OWNER TO ADMIN;
            ALTER TABLE PENDING_INVITATIONS_SEQ OWNER TO ADMIN;
            ALTER SEQUENCE PENDING_INVITATIONS_SEQ OWNED BY PENDING_INVITATIONS.INVITATION_ID;            
			ALTER TABLE ONLY PENDING_INVITATIONS ADD CONSTRAINT PENDING_INVITATIONS_PK PRIMARY KEY(INVITATION_ID);
			ALTER TABLE PENDING_INVITATIONS ADD CONSTRAINT PENDING_INVITATIONS_FK1 FOREIGN KEY (ROLE_ID) REFERENCES ROLES (ROLE_ID);
            SELECT PG_CATALOG.SETVAL('PENDING_INVITATIONS_SEQ', 1, TRUE);

CREATE TABLE PASSWORD_CHANGE_REQUESTS (
	PASSWORD_CHANGE_REQUEST_ID BIGINT NOT NULL,
	USER_ID BIGINT NOT NULL,
	CREATION_DATE TIMESTAMPTZ NOT NULL
);

            ALTER TABLE PASSWORD_CHANGE_REQUESTS OWNER TO ADMIN;
            ALTER TABLE ONLY PASSWORD_CHANGE_REQUESTS ADD CONSTRAINT PASSWORD_CHANGE_REQUESTS_PK PRIMARY KEY(ID);
			ALTER TABLE PASSWORD_CHANGE_REQUESTS ADD CONSTRAINT PASSWORD_CHANGE_REQUESTS_FK1 FOREIGN KEY (USER_ID) REFERENCES USERS (USER_ID);
